val();	\r\n	  	  \r\n      if(!/.(gif|jpg|jpeg|png)$/.test(filepath)){\r\n			tipStatus(\'fail\',\'图片格式错误\'); \r\n			return false;\r\n      }  	\r\n\r\n	  if(this.files){\r\n    		var file = this.files[0];\r\n    		if(file.size>1*1024*1024){\r\n	        	tipStatus(\'fail\',\'文件大小超过限制\'); \r\n	        	this.value=\'\';\r\n	        	return false;	  	\r\n	  		}\r\n	  }\r\n	  \r\n    $.ajaxFileUpload({\r\n            url: \"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => userinfo,\'act\' => headerPrepare)); ?>\", //用于文件上传的服务器端请求地址\r\n            secureuri: false, //是否需要安全协议，一般设置为false\r\n            fileElementId: \'fileField\', //文件上传域的ID\r\n            dataType: \'json\', //返回值类型 一般设置为json\r\n            success: function (data)  //服务器成功响应处理函数\r\n            {\r\n            	$(\'#hidden_image_id\').val(data.image_id)\r\n            	$(\'#preview\').attr(\'src\',data.src);\r\n            },\r\n            error: function (data)//服务器响应失败处理函数\r\n            {\r\n                tipStatus(\'fail\',\'服务器无响应\'); \r\n            }\r\n     })  \r\n})\r\n</script>\r\n\r\n\r\n\";', '1432021799', '0');
