sp;\r\n			  	<img src=\"<?php echo kernel::router()->gen_url(array(\'app\' => \'front\',\'ctl\' => \'passport\',\'act\' => \'gen_vcode\')); ?>\" title=\"看不清？点击换一张\" alt=\"验证码\" class=\"captcha\" id=\"captcha\"/>\r\n				<label style=\"display: none;\" class=\"captcha_msg\">请填写验证码</label>\r\n			</div>\r\n			<div class=\"verify_mess\">\r\n				<div class=\"fl_l\">\r\n					<input type=\"checkbox\" name=\"front_user[agree]\" class=\"fl_l\" id=\"agree\" checked=\"checked\"/>\r\n				</div>\r\n				<div class=\"fl_l\">\r\n					<p class=\"hack\" title=\"点击阅读校中店协议\">我已认真阅读并同意<a onclick=\"readAgreement();\" id=\"protocol\" class=\"blue\" href=\"#\">《校中店协议》</a></p>\r\n				</div>\r\n				<div class=\"clear\"></div>\r\n				<div class=\"registered\">\r\n					<a href=\"javascript:void(0);\" id=\"register\">注册</a>\r\n				</div>\r\n			</div>\r\n		</div>\r\n		</form>\r\n	</div>\r\n	<div style=\"padding:10px;display:none\" id=\"agreement\">校中店协议<div class=\"btnt\">\r\n	<input class=\"btn-img\" type=\"\" onclick=\"protocolReg();\" value=\"同意并继续\">\r\n	</div></div>\r\n	<div class=\"footer\">©Copyright all rights reserved for China International Book Trading Group Co., Ltd.</div>\r\n</body>\r\n\r\n<script type=\"text/javascript\">\r\n<?php if( !empty($this->_vars[\'school\']) ){ ?>\r\n$(window).load(function() {\r\n	getSelectVal(); \r\n}); \r\n<?php }  if( !empty($this->_vars[\'school_arr\']) ){ ?>\r\n$(\"#sid\").change(function(){ \r\n	getSelectVal(); \r\n});\r\n<?php } ?>\r\n\r\nfunction getSelectVal(){ \r\n    var sid = $(\"#sid\").val();\r\n	if(sid == \'\' || sid == \"学校\"){\r\n		$(\"#department\").empty();\r\n		$(\"#department\").append(\"<option>院系</option>\");\r\n\r\n		$(\".department_msg\").html(\'请选择学校和院系\');\r\n		$(\".department_msg\").hide();\r\n	}else{\r\n		$.ajax({ \r\n			url: \"<?php echo kernel::router()->gen_url(array(\'app\' => \'front\',\'ctl\' => \'passport\',\'act\' => \'getdepartment\')); ?>\", \r\n			type: \"post\",\r\n			cache:false,\r\n			dataType: \"json\",\r\n			data:{sid:sid},\r\n			success: function(data){\r\n				if(data == 0){//当前学校没有院系\r\n					$(\"#department\").empty();\r\n					$(\"#department\").append(\"<option>院系</option>\");\r\n					$(\".department_msg\").html(\'当前学校没有院系,请重新选择\');\r\n					$(\".department_msg\").show();\r\n				}else{\r\n					$(\"#department\").empty();\r\n					$.each(data, function(i, item){\r\n						var option = \"<option value=\'\"+item[\'id\']+\"\'>\"+item[\'department\']+\"</option>\"; \r\n				  　　   $(\"#department\").append(option); \r\n					});\r\n					\r\n					$(\".department_msg\").html(\'请选择学校和院系\');\r\n					$(\".department_msg\").hide();\r\n				}\r\n			}\r\n		});\r\n	}\r\n}\r\n\r\n\r\nfunction readAgreement(){\r\n	layer.open({\r\n		type: 1,\r\n		move: false,\r\n		title: \'校中店协议\',\r\n		area: [\'600px\', \'700px\'], //宽高\r\n		content: $(\"#agreement\").html()\r\n	}); \r\n}\r\n\r\nfunction protocolReg(){\r\n	//var index = layer.open();\r\n	layer.closeAll();\r\n}\r\n\r\n$(\"input[type=\'text\'], input[type=\'password\']\").focus(\r\n	function(){\r\n		$(this).css({border:\"1px solid #6fb9ff\"})\r\n	}).blur(\r\n	function(){\r\n		$(this).css({border:\"1px solid #dadada\"})\r\n});\r\n \r\n$(\"#username\").focusout(function() {\r\n	var pattern = /^([0-9A-Za-z_]|[\\u4e00-\\u9fa5]){4,18}$/;\r\n	var username = $.trim($(\"#username\").val());\r\n	if(pattern.test(username) && username != \"\"){\r\n		//用户名格式正确，后台验证用户名是否唯一\r\n		$.ajax({ \r\n			url: \"<?php echo kernel::router()->gen_url(array(\'app\' => \'front\',\'ctl\' => \'passport\',\'act\' => \'checkName\')); ?>\", \r\n			type: \"post\",\r\n			dataType: \"json\",\r\n			data:{username:username},\r\n			success: function(msg){\r\n				if(msg.res == 0){//用户名已存在,显示登录链接\r\n					$(\".dl\").show();\r\n					$(\".username_msg\").hide();\r\n					return false;\r\n				}else{\r\n					$(\".dl\").hide();\r\n					$(\".username_msg\").hide();\r\n				}\r\n			}, \r\n		});\r\n	}else{//用户名格式不正确\r\n		$(\".username_msg\").show();\r\n		$(\".dl\").hide();\r\n		return false;\r\n	}\r\n})\r\n\r\n$(\"#truename\").focusout(function() {\r\n	var pattern = /^([0-9A-Za-z_]|[\\u4e00-\\u9fa5]){2,10}$/;\r\n	var truename = $.trim($(\"#truename\").val());\r\n	if(pattern.test(truename) && truename != \"\"){\r\n		$(\".truename_msg\").hide();\r\n	}else{\r\n		$(\".truename_msg\").show();\r\n		$(\".truename_msg\").html(\'真实姓名长度在2-10位之间\');\r\n	}\r\n})\r\n\r\n$(\"#password\").focusout(function() {\r\n       var pattern = /^[0-9A-Za-z_]{4,16}$/;\r\n       var pwd1 = $.trim($(\"#password\").val());\r\n       if(pattern.test(pwd1)&&pwd1!=\"\"){\r\n      	   //后台返回数据验证数据是否合法\r\n      	   $(\".password_msg\").hide();\r\n      }else{\r\n		$(\".password_msg\").text(\'密码长度为4~16位字符\');\r\n      	$(\".password_msg\").show();\r\n      }\r\n})\r\n\r\n$(\"#password2\").focusout(function() {       \r\n       var pwd1 = $.trim($(\"#password\").val());\r\n       var pwd2 = $.trim($(\"#password2\").val());\r\n	   if(pwd1==pwd2&&pwd2!=\"\"){\r\n      	   //后台返回数据验证数据是否合法\r\n      	   $(\".password2_msg\").hide();\r\n        }else{      	\r\n      	$(\".password2_msg\").show();\r\n      }\r\n})\r\n\r\n$(\"#email\").focusout(function() {\r\n       var pattern = /^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+.([a-z]{2,3})$/;\r\n       var u_email = $.trim($(\"#email\").val());\r\n       if(pattern.test(u_email)&&u_email!=\"\"){\r\n      	   //后台返回数据验证数据是否合法\r\n      	   $(\".email_msg\").hide();\r\n      }else{      	\r\n      	$(\".email_msg\").show();\r\n      }\r\n})\r\n\r\n$(\"#mobile\").focusout(function() {\r\n	var pattern = /^((13|15|17|18)+\\d{9})$/;\r\n	var u_phone = $.trim($(\"#mobile\").val());\r\n	if(pattern.test(u_phone)&&u_phone!=\"\"){\r\n		//后台返回数据验证数据是否合法\r\n		$(\".mobile_msg\").hide();\r\n	}else{\r\n		$(\".mobile_msg\").show();\r\n	}\r\n})\r\n\r\n$(\"#code\").focusout(function() {       \r\n	var code = $.trim($(\"#code\").val());\r\n	if(code == \'\'){\r\n		$(\".code_msg\").hide();return false;\r\n	}else{      	\r\n		$(\".code_msg\").show();\r\n	}\r\n})\r\n\r\n$(function() 