rname.test(username)){//用户名格式不正确\r\n		$(\".username_msg\").show();\r\n		return false;\r\n	}\r\n	if(!pattern_truename.test(truename)){\r\n		$(\".truename_msg\").show();\r\n		$(\".truename_msg\").html(\'真实姓名长度在2-10位之间\');\r\n		return false;\r\n	}\r\n	if(password == \'\'){\r\n		$(\".password_msg\").show();\r\n		$(\".password_msg\").html(\'密码不能为空\');\r\n		return false;\r\n	}\r\n	if(password.length<4 || password.length>16){\r\n		$(\".password_msg\").show();\r\n		$(\".password_msg\").html(\'密码长度在4-16位之间\');\r\n		return false;\r\n	}\r\n	if(password2 == \'\'){\r\n		$(\".password2_msg\").show();\r\n		return false;\r\n	}\r\n	if(password2 != password){\r\n		$(\".password2_msg\").show();\r\n		return false;\r\n	}\r\n	if(department == \'\' || department == \'院系\'){\r\n		$(\".department_msg\").show();\r\n		return false;\r\n	}else{\r\n		$(\".department_msg\").hide();\r\n	}\r\n	if(email == \'\'){\r\n		$(\".email_msg\").show();\r\n		$(\".email_msg\").html(\'邮箱不能为空\');\r\n		return false;\r\n	}\r\n	if(!/^\\w+([-+.]\\w+)*@\\w+([-.]\\w+)*\\.\\w+([-.]\\w+)*$/.test(email)){\r\n		$(\".email_msg\").show();\r\n		$(\".email_msg\").html(\'请输入正确的邮箱地址\');\r\n		return false;\r\n	}\r\n	if(mobile == \'\'){\r\n		$(\".mobile_msg\").show();\r\n		$(\".mobile_msg\").html(\'手机号码不能为空\');\r\n		return false;\r\n	}\r\n	if(!mobile.match(/^((13|15|17|18)+\\d{9})$/)) {\r\n		$(\".mobile_msg\").show();\r\n		$(\".mobile_msg\").html(\'请填写正确的手机格式\');\r\n		return false;\r\n	} \r\n	if(code == \'\'){\r\n		$(\".code_msg\").show();\r\n		$(\"#code\").focus();return false;\r\n	}\r\n	if(verifycode == \'\'){\r\n		$(\".captcha_msg\").show();\r\n		$(\"#verifycode\").focus();return false;\r\n	}\r\n	if(agree == false){\r\n		layer.alert(\'注册前请仔细阅读校中店协议注册\');return false;\r\n	}\r\n\r\n	$.ajax({ \r\n		url: \"<?php echo kernel::router()->gen_url(array(\'app\' => \'front\',\'ctl\' => \'passport\',\'act\' => \'checkReg\')); ?>\", \r\n		type: \"post\",\r\n		cache:false,\r\n		dataType: \"json\",\r\n		data:{username:username,mobile:mobile