INSERT INTO `sdb_base_kvstore` VALUES ('356', 'cache/template', '02bac417b2b431c633d1e0be9c502be2', 's:11116:\"<div class=\"blank68\"></div>\r\n<div class=\"c2\">\r\n\r\n	<div class=\"title\">我的预选目录<span>(<?php echo $this->_vars[\'prepare_num\']; ?>)</span><!--<div class=\"onblur\"></div>--></div>\r\n    \r\n    \r\n    \r\n	<div class=\"content\">\r\n  \r\n   		<div class=\"preheader\">\r\n        	<ul>\r\n            	<li style=\"width:80px;\">\r\n                	<input type=\"checkbox\" class=\"fathercheck\" name=\"fathercheck\" id=\"asdasd\"/>\r\n                	<input type=\"hidden\" id=\"realcheckbox\" value=\"0\">\r\n                	<span class=\"chooseall\">全选</span>\r\n                </li>\r\n             	<li style=\"width:430px;\">书名</li>\r\n            	<li style=\"width:160px;\">单价（元）</li>\r\n            	<li style=\"width:160px;\">数量</li>\r\n            	<li style=\"width:156px;\">金额（元）</li>\r\n                <li style=\"width:202px;\">操作</li>                                                           	\r\n            </ul>\r\n        </div>\r\n    	<!-- preheader end -->\r\n \r\n    	<form action=\"<?php echo kernel::router()->gen_url(array(\'app\' => orders,\'ctl\' => prepare,\'act\' => confirm)); ?>\" method=\"POST\" id=\"prepare_order\">\r\n    	<input type=\"hidden\" name=\"data\" id=\"manydata\" value=\" \">\r\n    	<div class=\"prebody\">\r\n    		<!-- each -->\r\n    		<?php if($this->_vars[\'data_book\'])foreach ((array)$this->_vars[\'data_book\'] as $this->_vars[\'book\']){ ?>\r\n        	<div class=\"itemone\">\r\n            	<ul>\r\n                	<li class=\"first\"><input type=\"checkbox\" class=\"soncheck\" id=\"<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\"/></li>\r\n                	<li style=\"width:430px;\">\r\n                    	<div class=\"book_img\"><a href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => book,\'act\' => index,\'id\' => $this->_vars[\'book\'][\'book_id\'])); ?>\" target=\"_blank\"><img src=\"<?php echo $this->_vars[\'book\'][\'image_url\']; ?>\" width=\"90px\" height=\"90px\" /></a></div>\r\n                        <div class=\"book_name\"><a href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => book,\'act\' => index,\'id\' => $this->_vars[\'book\'][\'book_id\'])); ?>\"  target=\"_blank\"><?php echo $this->_vars[\'book\'][\'title\']; ?></a></div>\r\n                    </li>\r\n                	<li style=\"width:160px;\">\r\n                    	<div class=\"price-content\">\r\n                            <div class=\"price-original\"><?php echo $this->_vars[\'book\'][\'list_price\']; ?></div>\r\n                            <div class=\"price-now\" id=\"price-now-<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\"><?php echo $this->_vars[\'book\'][\'real_price\']; ?></div>\r\n                        </div>    \r\n                    </li>        \r\n                	<li style=\"width:160px;\">\r\n                    	<div class=\"td-inner\">\r\n                            <div class=\"item-amount\">\r\n                                <a href=\"javascript:void(0)\" class=\"no-minus\" id=\"minus<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\">-</a>\r\n                                    <input value=\"<?php echo $this->_vars[\'book\'][\'num\']; ?>\" class=\"text text-amount\" data-max=\"89\" data-now=\"1\" autocomplete=\"off\" type=\"text\" id=\"num<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\">\r\n                                <a href=\"javascript:void(0)\" class=\"plus\" id=\"plus<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\">+</a>                        \r\n                            </div>\r\n                        </div>\r\n                    </li>\r\n                	<li style=\"width:156px;\">\r\n                    	<div class=\"td-inner\">\r\n                        	<div class=\"item-sum\" id=\"item-sum-<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\"><?php echo $this->_vars[\'book\'][\'item_sum\']; ?></div>\r\n                        </div>\r\n                    </li>\r\n                    <li style=\"width:202px;\">\r\n                    	<div class=\"td-inner td-op\">                 \r\n                    	\r\n                    		<?php if( $this->_vars[\'book\'][\'collect\'] == 1 ){ ?>\r\n                    			<a href=\"javascript:void(0)\">已收藏</a>                    		\r\n                    		<?php }else{ ?>   	\r\n                    			<a href=\"javascript:void(0)\" class=\"movetocollect\" id=\"<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\">移入收藏夹</a>\r\n                    		<?php } ?>\r\n                    		\r\n                            <a href=\"javascript:void(0)\" class=\"cancelprepare\" id=\"<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\">删除</a>\r\n                        </div>\r\n                    </li>                                                                        \r\n                </ul>\r\n            </div>\r\n            <?php } ?>\r\n            <!-- each -->\r\n            \r\n        </div>\r\n    </form>    \r\n   	<!-- prebody end -->\r\n   \r\n   		<div class=\"prefooter\">\r\n            	<div class=\"select-all\">\r\n                     <input type=\"checkbox\" class=\"fathercheck\" name=\"fathercheck\" /><span class=\"chooseall\">全选</span>\r\n                </div>\r\n                <div class=\"operations\">\r\n                    <a href=\"javascript:void(0)\" id=\"cancelallprepare\">\r\n                        删除\r\n                    </a>\r\n                    <a href=\"javascript:void(0)\" class=\"addallcollect\">\r\n                        移入收藏夹\r\n                    </a>\r\n                    <a href=\"javascript:void(0)\" class=\"allshare\">\r\n                        写推荐\r\n                    </a>\r\n                </div>\r\n                <div class=\"float-bar-right\">\r\n                    <div class=\"amounts\">\r\n                        <div class=\"amount-sum\">已选图书<em id=\"ItemsCount\">0</em>本</div>\r\n                        <div class=\"pipe\"></div>\r\n                        <div class=\"price-sum\">\r\n                            <span class=\"txt\">合计:</span><strong class=\"price\">¥<em id=\"J_Total\">0.00</em></strong>\r\n                        </div>\r\n                    </div>    \r\n                    <div class=\"btn-area\">\r\n                        <a href=\"javascript:void(0)\" class=\"toprepare\" id=\"toprepare\">提交预选</a>\r\n                    </div>\r\n                </div>    \r\n        </div>\r\n    </div>\r\n</div>\r\n\r\n<div class=\"blank68\"></div>\r\n\r\n<script>\r\n//初始化预选目录\r\nvar intPrepare = {\r\n	GetCheckedId:function(){            //获取选中的商品id及数量\r\n		var objcheck = $(\".soncheck:checked\");\r\n		var objlength = objcheck.length;\r\n		if(objlength == 0){\r\n			return \'\';\r\n		}else{\r\n			var countdata = new Array();\r\n			for(var i=0; i<objlength ; i++){\r\n				var id = $(objcheck[i]).attr(\'id\');\r\n				var num = $(\'#num\'+id).val();\r\n				countdata[i] = new Array(id, num); \r\n			}\r\n			str = $.toJSON(countdata);		\r\n			return str;\r\n		}\r\n	},\r\n	CountPre:function(){                //计算结果\r\n		var objcheck = $(\".soncheck:checked\");\r\n		var objlength = objcheck.length;\r\n		var str = this.GetCheckedId();\r\n		if(str == \'\'){\r\n			$(\'.toprepare\').removeClass(\'active\');\r\n			$(\'#ItemsCount\').html(0);\r\n			$(\'#J_Total\').html(0);		\r\n		}else{\r\n			$(\'.toprepare\').addClass(\'active\');\r\n		 	$.ajax({ \r\n				url: \"<?php echo kernel::router()->gen_url(array(\'app\' => \'orders\',\'ctl\' => \'prepare\',\'act\' => \'countPre\')); ?>\", \r\n				type: \"post\",\r\n				dataType: \"json\",\r\n				data:\'data=\'+str,\r\n				success: function(data){					\r\n					$(\'#ItemsCount\').html(data.totalnum);\r\n					$(\'#J_Total\').html(data.totalprice);\r\n		    	}\r\n	    	});		\r\n		}		\r\n	},\r\n	RowPrice:function(id){             //计算每行的总价\r\n		var oneprice = parseFloat($(\'#price-now-\'+id).html());\r\n		var onenum = parseInt($(\'#num\'+id).val());\r\n		var rowtotalprice = roundNumber(parseFloat(oneprice*onenum),2);\r\n		$(\'#item-sum-\'+id).html(rowtotalprice);		\r\n	}\r\n};\r\n\r\n$(\'.fathercheck\').each(function(){\r\n	$(this).on(\'click\',function(){\r\n		if($(\'#realcheckbox\').val()==0){\r\n			$(\'.fathercheck\').prop(\"checked\",true);\r\n			$(\'.soncheck\').prop(\"checked\",true);\r\n			$(\'.itemone\').addClass(\'checkbg\');\r\n			$(\'#realcheckbox\').val(1)\r\n		}else{\r\n			$(\'.fathercheck\').prop(\"checked\",false);\r\n			$(\'.soncheck\').prop(\"checked\",false);		\r\n			$(\'.itemone\').removeClass(\'checkbg\');			\r\n			$(\'#realcheckbox\').val(0)\r\n		}		\r\n		intPrepare.CountPre();\r\n	})\r\n})\r\n\r\n$(\'.soncheck\').each(function(){\r\n	$(this).on(\'click\',function(){\r\n		if($(this).prop(\"checked\")== true){\r\n			$(this).parent(\'li\').parent(\'ul\').parent(\'.itemone\').addClass(\'checkbg\');			\r\n		}else{\r\n			$(this).parent(\'li\').parent(\'ul\').parent(\'.itemone\').removeClass(\'checkbg\');				\r\n		}\r\n		intPrepare.CountPre();\r\n	})\r\n})\r\n\r\n$(\'.plus\').on(\'click\',function(){\r\n	var id = $(this).attr(\'id\');\r\n	var numid = id.replace(/plus/,\"num\");\r\n	temp = parseInt($(\'#\'+numid).val());	\r\n	id = id.replace(/plus/,\"\");\r\n	if(temp < 999){\r\n		$(\'#\'+numid).val(temp+1);\r\n		target_url = \"<?php echo kernel::router()->gen_url(array(\'app\' => \'orders\',\'ctl\' => \'prepare\',\'act\' => \'addPrepare\')); ?>\";\r\n		$.ajax({ \r\n			url: target_url, \r\n			type: \"post\",\r\n			dataType: \"json\",\r\n			data:\'id=\'+id,\r\n			success: function(data){\r\n				\r\n	    	}\r\n		});			\r\n	}else{\r\n		$(\'#\'+numid).val(999);	\r\n	}\r\n	intPrepare.RowPrice(id);	\r\n	intPrepare.CountPre();\r\n})\r\n\r\n$(\'.no-minus\').on(\'click\',function(){\r\n	var id = $(this).attr(\'id\');\r\n	var numid = id.replace(/minus/,\"num\");\r\n	temp = parseInt($(\'#\'+numid).val());\r\n	id = id.replace(/minus/,\"\");	\r\n	if(temp <= 1){\r\n		$(\'#\'+numid).val(1);\r\n	}else{\r\n		num = temp-1;\r\n		var target_url = \"<?php echo kernel::router()->gen_url(array(\'app\' => \'orders\',\'ctl\' => \'prepare\',\'act\' => \'updatePre\')); ?>\";		\r\n		$.ajax({ \r\n			url: target_url, \r\n			type: \"post\",\r\n			dataType: \"json\",\r\n			data:\'id=\'+id+\"&num=\"+num,\r\n			success: function(data){\r\n\r\n	    	}\r\n		});					\r\n		$(\'#\'+numid).val(num);\r\n	}\r\n	intPrepare.RowPrice(id);\r\n	intPrepare.CountPre();\r\n})\r\n\r\n$(\'.text-amount\').on(\'change\',function(){\r\n	var rightnum=new RegExp(\"^[0-9]*$\");\r\n	if($(this).val()<1){\r\n		$(this).val(1);\r\n	}else if($(this).val()>1000){\r\n		$(this).val(999);\r\n	}else if(!rightnum.test($(this).val())){\r\n		$(this).val(1);\r\n	}\r\n	var tempid = $(this).attr(\'id\');\r\n	var id = tempid.replace(/num/,\"\");		\r\n	num = $(this).val();\r\n	var target_url = \"<?php echo kernel::router()->gen_url(array(\'app\' => \'orders\',\'ctl\' => \'prepare\',\'act\' => \'updatePre\')); ?>\";		\r\n	$.ajax({ \r\n		url: target_url, \r\n		type: \"post\",\r\n		dataType: \"json\",\r\n		data:\'id=\'+id+\"&num=\"+num,\r\n		success: function(data){\r\n\r\n    	}\r\n	});			\r\n	intPrepare.RowPrice(id);	\r\n	intPrepare.CountPre();\r\n})\r\n\r\n$(\'#cancelallprepare\').on(\'click\',function(){\r\n	var str = intPrepare.GetCheckedId();\r\n	if(str ==\'\'){\r\n		tipStatus(\'fail\',\"没有选择图书\");\r\n	}else{\r\n	 	$.ajax({ \r\n			url: \"<?php echo kernel::router()->gen_url(array(\'app\' => \'orders\',\'ctl\' => \'prepare\',\'act\' => \'cancelAllprepare\')); ?>\", \r\n			type: \"post\",\r\n			dataType: \"json\",\r\n			data:\'data=\'+str,\r\n			success: function(data){					\r\n				if(data.code==1){\r\n					tipStatus(\'succ\',data.msg);\r\n				}else{\r\n					tipStatus(\'fail\',data.msg);\r\n				}\r\n	    	}\r\n    	});			\r\n	}\r\n})\r\n\r\n$(\'#toprepare\').on(\'click\',function(){\r\n	if($(this).hasClass(\'active\')){\r\n		var str = intPrepare