INSERT INTO `sdb_base_kvstore` VALUES ('451', 'cache/template', 'ac9b2e73954232fe495b43c8c641ce74', 's:2280:\"<?php $this->__view_helper_model[\'base_view_helper\'] = kernel::single(\'base_view_helper\'); ?><div class=\"add_department_form\" style=\"margin:20px;\">\r\n	<form action=\"<?php echo kernel::router()->gen_url(array(\'app\' => preset,\'ctl\' => admin_department,\'act\' => addNew)); ?>\" method=\"post\" id=\"add_department\">\r\n		所属学校：<select class=\"se_2\" id=\"sid\" name=\"department[sid]\" style=\"padding:5px 10px;\">\r\n						<option value=\"<?php echo $this->_vars[\'school_info\'][\'sid\']; ?>\"><?php echo $this->_vars[\'school_info\'][\'name\']; ?></option>\r\n				  </select>\r\n		<div>\r\n		<div class=\"clean\" style=\"height:15px\"></div>\r\n		院系名称：<input type=\"text\" vtype=\"required\" id=\"department\" name=\"department[department]\" class=\"department\" style=\"padding:5px 10px;\"/>\r\n		</div>\r\n	</form>\r\n\r\n	<?php $this->_tag_stack[] = array(\'area\', array(\'inject\' => \'.mainFoot\')); $this->__view_helper_model[\'base_view_helper\']->block_area(array(\'inject\' => \'.mainFoot\'), null, $this); ob_start(); ?>\r\n		<div class=\"table-action\">\r\n			<?php echo $this->ui()->button(array(\'type\' => \"submit\",\'label\' => kernel::single(\'base_view_helper\')->modifier_t($this->_vars[\'___preset\']=\"保存\",\'preset\'),\'id\' => \'department-add-form-submit\'));?>\r\n		</div>\r\n	<?php $_block_content = ob_get_contents(); ob_end_clean(); $_block_content = $this->__view_helper_model[\'base_view_helper\']->block_area($this->_tag_stack[count($this->_tag_stack) - 1][1], $_block_content, $this); echo $_block_content; array_pop($this->_tag_stack); $_block_content=\'\'; ?>\r\n</div>\r\n\r\n<script>\r\n(function(){\r\n    var _form = $(\'add_department\');\r\n    var btn =$(\'department-add-form-submit\');\r\n    var finder = finderGroup[\'<?php echo $_GET[\'_finder\'][\'finder_id\']; ?>\'];\r\n    \r\n	_form.store(\'target\',{\r\n		onSuccess:functi