INSERT INTO `sdb_base_kvstore` VALUES ('462', 'cache/template', '81118c818a94405937ba4b599b39e37e', 's:5223:\" 	<div class=\"blank68\"></div>\r\n	<div class=\"guess_con\">\r\n        <div class=\"guess_nav\">\r\n           <ul>\r\n               <div>推荐</div>\r\n               <li><a href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => guess,\'act\' => index)); ?>\">偏好推荐</a></li>\r\n               <li><a href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => enjoy,\'act\' => index)); ?>\">猜你喜欢</a></li>\r\n               <li class=\"font_s\"><a href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => index)); ?>\">我来推荐</a></li>\r\n               <li><a href=\"\">偏好设置</a></li>\r\n           </ul>\r\n        </div>\r\n		\r\n        <div class=\"share_recommend\">\r\n        	<div class=\"title\">\r\n            	<?php echo $this->_vars[\'title\']; ?>\r\n            </div>\r\n            \r\n            <div class=\"content\">\r\n            \r\n            	<div class=\"album\">\r\n                	<a class=\"common-add\" id=\"share-add\">\r\n                    	<i>&nbsp;</i>\r\n                        <span>选书</span>\r\n                    </a>\r\n                </div>\r\n                <form role=\"form\" id=\"shareform\" action=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => doShare)); ?>\" method=\"POST\">\r\n			    <div class=\"album-list\" style=\"display:none;\">\r\n					<div class=\"item-l\">\r\n                    	<img src=\"<?php echo $this->_vars[\'res_url\']; ?>/img/book/books.jpg\" id=\"book_img\" width=\"140\" height=\"140\"/>\r\n                    </div>\r\n                    <div class=\"item-r\">\r\n                    	<div class=\"item-meta\">\r\n                        	<p class=\"bookname\" id=\"bookname\"></p>\r\n                            <p id=\"author\"></p>\r\n                        </div>\r\n                        <div class=\"group-textarea\">\r\n							<b class=\"fluid-input-inner\">\r\n                        		<textarea placeholder=\"为您想推荐的书说几句！\" class=\"fluid-input-text\" name=\"content\" onkeyup=\"javascript:checkWord(this);\" onmousedown=\"javascript:checkWord(this);\" id=\"content\"></textarea>\r\n                        		<input type=\"hidden\" id=\"book_id\" value=\"\" name=\"book_id\"/>\r\n                    		</b>\r\n                            <span class=\"form-indicator\">\r\n                            	<b class=\"form-indicator-count\" id=\"wordCheck\">0</b>/600字\r\n                            </span>\r\n                        </div>\r\n                        <div class=\"savearea2\">\r\n            				<button id=\"user-changepwd-form-submit\" class=\"btn btn-primary\" type=\"button\">发&nbsp;布</button>\r\n            			</div>\r\n                    </div>\r\n                </div>\r\n            	</form>\r\n            </div>\r\n        \r\n        \r\n        </div>\r\n   </div>\r\n   \r\n   \r\n<script>\r\n   \r\n $(\'#share-add\').on(\'click\',function(){\r\n	var shareaddstep1 = layer.open({\r\n		type: 2,\r\n		title: \'选择想推荐的书\',\r\n		shadeClose: false,\r\n		shade: 0.6,\r\n		area: [\'640px\', \'420px\'],\r\n		scrollbar: false,\r\n		content: \"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => shareAdd)); ?>\"\r\n	}); 									\r\n})  \r\n\r\nvar maxstrlen = 600;\r\nfunction checkWord(c) {\r\n    len = maxstrlen;\r\n    var str = c.value;\r\n    myLen = getStrleng(str);\r\n    var wck = $(\"#wordCheck\");\r\n    if (myLen > len * 2) {\r\n        c.value = str.substring(0, i + 1);\r\n    }\r\n    else {\r\n        wck.html(Math.floor(myLen/2));\r\n    }\r\n}\r\nfunction getStrleng(str) {\r\n    myLen = 0;\r\n    i = 0;\r\n    for (; (i < str.length) && (myLen <= maxstrlen * 2); i++) {\r\n        if (str.charCodeAt(i) > 0 && str.charCodeAt(i) < 128)\r\n            myLen++;\r\n        else\r\n            myLen += 2;\r\n    }\r\n    return myLen;\r\n}\r\n   \r\nfunction getBookId(BookId){	\r\n		layer.closeAll();\r\n		var loadindex = layer.load(4);	\r\n	 	$.ajax({ \r\n			url: \"<?php echo kernel::router()->gen_url(array(\'app\' => \'front\',\'ctl\' => \'share\',\'act\' => \'getBook\')); ?>\", \r\n			type: \"post\",\r\n			dataType: \"json\",\r\n			data:\'bid=\'+BookId,\r\n			success: function(data){					\r\n				layer.closeAll(); 	\r\n				$(\'.album-list\').css({\'display\':\'block\'});\r\n	        	$(\"#bookname\").html(data.title);\r\n	        	$(\"#book_img\").attr(\"src\",data.image_url);\r\n	        	$(\"#author\").html(\"by \"+data.author);\r\n	        	$(\"#book_id\").val(data.book_id)\r\n	    	}\r\n    	});\r\n\r\n}   \r\n    \r\n$(\'#user-changepwd-form-submit\').on(\'click\',function(){\r\n	var book_id = $(\"#book_id\").val();\r\n	var content = $.trim($(\"#content\").val());\r\n	if(book_id == \'\' || book_id == undefined || isNaN(book_id)){\r\n		layer.alert(\'非法参数\');\r\n		return false;\r\n	}\r\n	if(content.length == 0){\r\n		layer.alert(\'内容不能为空\');\r\n		return false;\r\n	}else if(content.length >= 600){\r\n		layer.alert(\'内容长度不能超过600个字符\');\r\n		return false;\r\n	}\r\n	$.ajax({ \r\n			url: \"<?php echo kernel::router()->gen_url(array(\'app\' => \'front\',\'ctl\' => \'share\',\'act\' => \'doShare\')); ?>\", \r\n			type: \"post\",\r\n			data:\'book_id=\'+book_id+\'&content=\'+content,\r\n			success: function(data){	\r\n				if(data==1){\r\n					tipStatus(\'succ\',\'推荐成功\',\'<?php echo kernel::router()->gen_url(array(\'app\' => \"front\",\'ctl\' => \"page\",\'act\' => \"index\")); ?>\');	\r\n				}else{\r\n					tipStatus(\'fail\',\'推荐失败\');\r\n				}\r\n	    	}\r\n    	});\r\n})     \r\n</script>\";', '1433290694', '0');
