iv class=\"publish-switcher-pannel cbook\" <?php if( $this->_vars[\'showtype\'] == \'ordersitem\' ){ ?> style=\"display:block;\" <?php }else{ ?> style=\"display:none;\"   <?php } ?> >\r\n        		<?php if( $this->_vars[\'fenye\'][\'ordersitem\'][\'pre\'] != \'no\' ){ ?>\r\n        			<a class=\"pre\" href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => shareAdd,\'showtype\' => 1,\'np\' => $this->_vars[\'fenye\'][\'ordersitem\'][\'pre\'])); ?>\"></a>\r\n        		<?php }  if( $this->_vars[\'fenye\'][\'ordersitem\'][\'next\'] != \'no\' ){ ?>\r\n        	    <a class=\"next\" href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => shareAdd,\'showtype\' => 1,\'np\' => $this->_vars[\'fenye\'][\'ordersitem\'][\'next\'])); ?>\"></a>   \r\n        	    <?php }  if($this->_vars[\'data_pordersitem\'])foreach ((array)$this->_vars[\'data_pordersitem\'] as $this->_vars[\'book\']){ ?>\r\n                <div class=\"prepare\">	\r\n                    	<div class=\"book_img\" id=\"<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\"><a href=\"javascript:void(0)\" title=\"<?php echo $this->_vars[\'book\'][\'title\']; ?>\"><img src=\"<?php echo $this->_vars[\'book\'][\'image_url\']; ?>\" width=\"80px\" height=\"80px\" /><i></i></a></div>\r\n                </div>\r\n                <?php } ?>   \r\n                <div class=\"savearea3\">  \r\n                	<button class=\"btn btn-primary btn-center choosebook\" type=\"button\">确&nbsp;定</button>\r\n                </div>             	\r\n            </div>\r\n        	<div class=\"publish-switcher-pannel cbook\" <?php if( $this->_vars[\'showtype\'] == \'prepare\' ){ ?> style=\"display:block;\" <?php }else{ ?> style=\"display:none;\"  <?php } ?> >       \r\n        		<?php if( $this->_vars[\'fenye\'][\'prepare\'][\'pre\'] != \'no\' ){ ?>\r\n        			<a class=\"pre\" href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => shareAdd,\'showtype\' => 2,\'np\' => $this->_vars[\'fenye\'][\'prepare\'][\'pre\'])); ?>\"></a>\r\n        		<?php }  if( $this->_vars[\'fenye\'][\'prepare\'][\'next\'] != \'no\' ){ ?>\r\n        	    <a class=\"next\" href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => shareAdd,\'showtype\' => 2,\'np\' => $this->_vars[\'fenye\'][\'prepare\'][\'next\'])); ?>\"></a>   \r\n        	    <?php }  if($this->_vars[\'data_book\'])foreach ((array)$this->_vars[\'data_book\'] as $this->_vars[\'book\']){ ?>\r\n                <div class=\"prepare\">	\r\n                    	<div class=\"book_img\" id=\"<?php echo $this->_vars[\'book\'][\'book_id\']; ?>\"><a href=\"javascript:void(0)\" title=\"<?php echo $this->_vars[\'book\'][\'title\']; ?>\"><img src=\"<?php echo $this->_vars[\'book\'][\'image_url\']; ?>\" width=\"80px\" height=\"80px\" /><i></i></a></div>\r\n                </div>\r\n                <?php } ?>       \r\n                <div class=\"savearea3\">  \r\n                	<button class=\"btn btn-primary btn-center choosebook\" type=\"button\">确&nbsp;定</button>\r\n                </div>             \r\n            </div>\r\n       		<div class=\"publish-switcher-pannel cbook\" <?php if( $this->_vars[\'showtype\'] == \'collect\' ){ ?> style=\"display:block;\" <?php }else{ ?> style=\"display:none;\"  <?php } ?> >\r\n        		<?php if( $this->_vars[\'fenye\'][\'collection\'][\'pre\'] != \'no\' ){ ?>\r\n        			<a class=\"pre\" href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => shareAdd,\'showtype\' => 3,\'np\' => $this->_vars[\'fenye\'][\'collection\'][\'pre\'])); ?>\"></a>\r\n        		<?php }  if( $this->_vars[\'fenye\'][\'collection\'][\'next\'] != \'no\' ){ ?>\r\n        	    <a class=\"next\" href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => shareAdd,\'showtype\' => 3,\'np\' => $this->_vars[\'fenye\'][\'collection\']