                     </td>\r\n	                             <td class=\"quantity\">\r\n	                             	 <i class=\"special-num\"><?php echo $this->_vars[\'item\'][\'nums\']; ?></i>\r\n	                             </td>    \r\n	                             <?php if( $this->_vars[\'index\'] == 0 ){ ?>\r\n								 <td class=\"amount order-reallast\" rowspan=\"<?php echo $this->_env_vars[\'foreach\'][\'items\'][\'total\']; ?>\">\r\n	                                  <p>\r\n	                                 	<em class=\"real-price special-num\">￥<?php echo $this->_vars[\'orders\'][\'order_amount\']; ?></em>\r\n	                                  </p>\r\n	                             </td>\r\n	                             <td class=\"trade-status order-reallast\" rowspan=\"<?php echo $this->_env_vars[\'foreach\'][\'items\'][\'total\']; ?>\">\r\n	                             	 <span class=\"J_MakePoint status\" ><?php echo $this->_vars[\'orders\'][\'orderStatus\']; ?></span>\r\n	                                 <span style=\"display:block; margin-bottom:10px;\"><a target=\"_blank\" href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => myorder,\'act\' => items,\'orderId\' => $this->_vars[\'orders\'][\'order_sn\'])); ?>\">订单详情</a></span>\r\n	                                 <?php if( $this->_vars[\'orders\'][\'pay_status\'] != 0 && $this->_vars[\'orders\'][\'ship_status\'] != 0 ){ ?>\r\n	                                 <span><a href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => myorder,\'act\' => tosp,\'orderId\' => $this->_vars[\'orders\'][\'order_sn\'])); ?>\">申请售后</a></span>\r\n	                                 <?php } ?>\r\n	                             </td>\r\n	                              <?php } ?>           \r\n	                             <td class=\"trade-operate\">\r\n	                             	 <?php if( $this->_vars[\'index\'] == 0 && $this->_vars[\'orders\'][\'pay_status\'] == 0 ){ ?><a href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => pay,\'act\' => index,\'order_id\' => $this->_vars[\'orders\'][\'order_sn\'])); ?>\" target=\"_blank\" class=\"paynow\">立即付款</a><?php } ?>								 \r\n<!--	                             	<a href=\"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => share,\'act\' => index,\'book_id\' => $this->_vars[\'item\'][\'book_id\'])); ?>\">写推荐</a>-->\r\n	                             </td>\r\n	                        </tr>\r\n	                        <!-- end -->\r\n	                        <?php } unset($this->_env_vars[\'foreach\'][items]); ?>\r\n	                    </tbody>\r\n	                    <!-- end -->\r\n	                    <?php }  } ?>\r\n                </table>\r\n            </div>\r\n\r\n			<!--分页begin-->\r\n			<?php if( $this->_vars[\'fenye\'][\'total_pages\'] > 1 ){ ?>\r\n			<div class=\"wb_fenye\" style=\"border:0;\">\r\n				 <div class=\"fenye\">\r\n					  <?php if( $this->_vars[\'fenye\'][\'pre\'] == \'no\' ){ ?><a class=\"page prev page_dis\" href=\"javascript:void(0)\">上一页</a><?php }else{ ?><a class=\"page prev\" href=\"<?php echo $this->_vars[\'fenye\'][\'pre_url\']; ?>\">上一页</a><?php }  if($this->_vars[\'fenye\'][\'pagenum\'])foreach ((array)$this->_vars[\'fenye\'][\'pagenum\'] as $this->_vars[\'pagenum\']){  if( $this->_vars[\'fenye\'][\'nowpage\'] == $this->_vars[\'pagenum\'][\'code\'] ){ ?>\r\n								<a href=\"<?php echo $this->_vars[\'pagenum\'][\'url\']; ?>\" class=\"page active\"><?php echo $this->_vars[\'pagenum\'][\'code\']; ?></a>\r\n						  <?php }else{ ?>\r\n							<a href=\"<?php echo $this->_vars[\'pagenum\'][\'url\']; ?>\" class=\"page\"><?php echo $this->_vars[\'pagenum\'][\'code\']; ?></a>\r\n						  <?php }  }  if( $this->_vars[\'fenye\'][\'lastpage\'] == \'yes\' ){ ?>\r\n						<a class=\"page page_dis\">…</a>\r\n						<a class=\"page\" href=\"<?php echo $this->_vars[\'fenye\'][\'lastpage_url\']; ?>\"><?php echo $this->_vars[\'fenye\'][\'total_pages\']; ?></a>	   \r\n					  <?php }  if( $this->_vars[\'fenye\'][\'next\'] == \'no\' ){ ?> <a class=\"page next page_dis\" href=\"javascript:void(0)\">下一页</a> <?php }else{ ?> <a href=\"<?php echo $this->_vars[\'fenye\'][\'next_url\']; ?>\" class=\"page next\">下一页</a> <?php } ?>\r\n				 </div>\r\n			</div>\r\n			<?php } ?>\r\n			<!--分页end-->          \r\n        </div>    \r\n    </div>\r\n</div>\r\n\r\n<div class=\"blank68\"></div>\r\n\r\n<script>\r\nvar oid_reg = /^\\d{14}$/;\r\n\r\nfunction orderSearch(id){\r\n	var keyword = $.trim($(\"#\"+id).val());\r\n	if(keyword.length < 1){\r\n		layer.alert(\'请输入查询关键词！\', {icon:5});\r\n		return false;\r\n	}\r\n\r\n	var url = \"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => myorder,\'act\' => search)); ?>\";\r\n	url += \"?keyword=\"+keyword;\r\n\r\n	window.location = url;\r\n}\r\n\r\n$(\'.order-cancel\').on(\'click\',function(e){\r\n	var clicked = $(e.target);\r\n	var oid = clicked.attr(\'oid\');\r\n\r\n	var index = layer.confirm(\'您确定要取消该订单吗？\', {\r\n			title:\'提示\',\r\n			shift:5,\r\n			icon: 3,\r\n			offset:\'300px\',\r\n			scrollbar: false,\r\n			move:false,\r\n			btn: [\'确认\',\'取消\']\r\n		}, function(){\r\n			if(oid_reg.test(oid)){\r\n				$.ajax({\r\n					url : \"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => myorder,\'act\' => cancelOrder)); ?>\", \r\n					type : \"post\",\r\n					dataType : \"json\",\r\n					data : {\"action\":\"cancel\", \'oid\':oid},\r\n					success: function(data){\r\n						if(data.code == 0){\r\n							close_all_layer();\r\n							tipStatus(\'fail\',\'删除失败\'); 						\r\n						}else{\r\n							close_all_layer();\r\n							tipStatus(\'succ\',\'删除成功\'); 	\r\n						}\r\n					}\r\n				})\r\n			}else{\r\n				layer.close(index);\r\n			}\r\n		}, function(){\r\n		   return false;\r\n		});\r\n})\r\n\r\n</script>\";', '1473153483', '0');
