\r\n				<input type=\"button\" name=\"\" id=\"save\" value=\"创建专题\" class=\"b-green\" action=\"addSpecial\"/>\r\n			</div>\r\n		</div>\r\n	</div>	\r\n</div>\r\n<script type=\"text/javascript\" src=\"<?php echo $this->_vars[\'res_url\']; ?>/js/ajaxfileupload.js\"></script>\r\n<script>\r\n	$(\'#save\').click(function(){\r\n		var name = $(\'#name\').val();\r\n		var view_id = $(\"input:radio:checked\").val();\r\n		var content = $(\'#content1\').val();\r\n		var listpic = $(\'#preview\').val();\r\n		var action = $(this).attr(\'action\');\r\n		var imgsrc = $(\'#imgid\').attr(\'src\');\r\n	\r\n		$.ajax({\r\n			type: \"POST\",\r\n			url : \"<?php echo kernel::router()->gen_url(array(\'app\' => \'front\',\'ctl\' => \'publish\',\'act\' => \'pageAdd\')); ?>\",\r\n			data : {\'name\':name,\'listpic\':listpic,\'view_id\':view_id,\'action\':action,\'content\':content,\'listimg\':imgsrc},\r\n			cache : false,\r\n			dataType : \"json\",\r\n			success : function(data){\r\n				if(data.code ==1){\r\n					var url = \'?page_id=\'+data.page_id+\'&view_id=\'+view_id;\r\n					tipStatus(\'succ\',data.msg,\"<?php echo kernel::router()->gen_url(array(\'ctl\' => \'publish\',\'act\' => \'pageEdit\',\'app\' => \'front\')); ?>\"+url);\r\n				}else{\r\n					tipStatus(\'fail\',data.msg);\r\n				}\r\n			},\r\n			error : function(){\r\n				alert(\'error\');\r\n			}\r\n		});\r\n	})\r\n\r\n	/*上传列表图片*/\r\n	$(document).on(\'change\',\'#fileField\',function(){\r\n		var filepath=$(\"input[name=\'file\']\").val();	\r\n		var preview = $(\'#preview\').val();\r\n		\r\n		if(preview == \'\'){\r\n			var odlimg = \'\';\r\n		}else{\r\n			var oldimg = $(\'#imgid\').attr(\'src\');\r\n		}\r\n		\r\n		if(!/.(gif|jpg|jpeg|png)$/.test(filepath)){\r\n			tipStatus(\'fail\',\'图片格式错误\'); \r\n			return false;\r\n		}  	\r\n\r\n		if(this.files){\r\n			var file = this.files[0];\r\n			if(file.size>1*512000){\r\n				tipStatus(\'fail\',\'文件大小超过限制\'); \r\n				this.value=\'\';\r\n				return false;	  	\r\n			}\r\n		}\r\n		  \r\n	    $.ajaxFileUpload({\r\n			url: \"<?php echo kernel::router()->gen_url(array(\'app\' => front,\'ctl\' => publish,\'act\' => uploadPic)); ?>\", //用于文件上传的服务器端请求地址\r\n			type: \'post\',\r\n			secureuri: false, //是否需要安全协议，一般设置为false\r\n			fileElementId: \'fileField\', //文件上传域的ID\r\n			dataType: \'json\', //返回值类型 一般设置为json\r\n			data : {\"oldimg\":oldimg},\r\n			success: function(data){\r\n				if(data.code == 1){\r\n					$(\'#imageshow\').html(\"<img width=\'380\' height=\'200\' id=\'imgid\' src=\'\"+data.src+\"\'>\");\r\n					$(\'#preview\').val(data.src);\r\n				}else{\r\n					tipStatus(\'fail\',data.info); \r\n				}\r\n			},\r\n			error: function (data){\r\n				tipStatus(\'fail\',\'服务器无响应\'); \r\n			}\r\n		})\r\n  \r\n	})\r\n</script>\";', '1473153517', '0');
